import { AbstractMesh } from './AbstractMesh';
import { Loop } from '../Loop';

class Sun extends AbstractMesh {
    constructor(loop) {
        super();
        this.getMesh();
        if (loop instanceof Loop) {
            this.join(loop);
        }
    }
    getMesh() {
        var sun = new THREE.Mesh(
            new THREE.SphereGeometry( 20000, 30, 30 ),
            new THREE.MeshBasicMaterial({color: 0xffffff, wireframe: true })
        );
        sun.position.z = -95000;
        sun.position.y = 2000;
        sun.position.x = -200;
        sun.visible = true;

        this.mesh = sun;
        return this.mesh;
    }
    animate() {

    }
}

export { Sun }